import React, {useState} from 'react'

function ListBox() {
    const [isOpen1, setIsOpen1] = useState(true);
    return (
      <div className="box">
        <button
          className="btn-toggle"
          onClick={() => setIsOpen1((open) => !open)}
        >
          {isOpen1 ? "–" : "+"}
        </button>
        {isOpen1 && <MovieList />}
      </div>
    );
  }
  function MovieList() {
    const [movies, setMovies] = useState(tempMovieData);
    return (
      <ul className="list">
        {movies?.map((movie) => (
          <Movie movie={movie}/>
        ))}
      </ul>
    );
  }

export default ListBox
